import { DataCallback, SubscriptionType } from "src/types";
/**
 * Managing the different streams of data
 */
declare const StreamsManager: () => {
    tap: <T>(identifier: string, state: T) => boolean;
    getStreamState: (identifier: string) => any;
    publish: <T_1>(identifier: string, data: T_1) => void;
    subscribeTo: <T_2>(identifier: string, onChange: DataCallback<T_2>) => SubscriptionType;
    reset: () => void;
};
export default StreamsManager;
